INSERT INTO `npc_emotes` VALUES (2526, 1399, 1, 0, 'Simpleton, you do not know who you provoke! I am the strongest of all the Veeshan\'s brood, none can match my power.');
INSERT INTO `npc_emotes` VALUES (2527, 1399, 2, 0, 'This, this is a shock. I, Zlandicar, brought down by such as you.');
