INSERT INTO `npc_emotes` VALUES (2547, 1413, 7, 2, 'I have risen once again.  Prepare to be sent to your grave!');
INSERT INTO `npc_emotes` VALUES (2548, 1413, 2, 0, 'You may have defeated me, but the hate of Innoruuk still burns throughout this realm!');
